mapper.select:
SELECT SINFO.id AS id, SINFO.sale_code AS saleCode, SINFO.sale_date AS saleDate, CSUPPLIER.cust_name AS custName, SINFO.currency AS currency, SINFO.exchange_rate AS exchangeRate, SINFO.total_sum AS totalSum, SINFO.discount_amount AS discountAmount, SINFO.receivable_accoun AS receivableAccoun, SINFO.due_date AS dueDate, DICTIONPAY.dict_name AS paymentStatus, SINFO.invoice_code AS invoiceCode, DICTIONPRO.dict_name AS saleProject, UINFO.real_name AS userName, SINFO.user_code AS userCode, SINFO.create_user AS createUser, logistics.logistic_company_name AS logisticsCompamyName, logistics.freight_code AS freightCode, SINFO.remarks AS remarks FROM tab_sale_info SINFO LEFT JOIN tab_sale_logistic_info logistics ON SINFO.sale_code = logistics.sale_code LEFT JOIN tab_dictionary_info DICTIONPAY ON SINFO.payment_status=DICTIONPAY.dict_code AND DICTIONPAY.data_type='collection_status' LEFT JOIN tab_dictionary_info DICTIONPRO ON SINFO.sale_project=DICTIONPRO.dict_code AND DICTIONPRO.data_type='project_list' LEFT JOIN tab_customer_supplier CSUPPLIER ON SINFO.cust_code=CSUPPLIER.cust_code LEFT JOIN tab_user_info UINFO ON SINFO.salesman_code = UINFO.salesman_code WHERE 1=1
AND SINFO.sale_code = #{saleCode}
AND SINFO.salesman_code=#{salesmanCode}
