mapper.select:
SELECT pinfo.purchase_date, pinfo.purchase_code, pinfo.user_code, uinfo.real_name, pinfo.cust_code, csinfo.cust_name, CASE WHEN goods.discount_rate > 0 THEN goods.discount_rate/100 else 1 END discountRate, IFNULL(goods.real_sell_price, 0) real_sell_price, goods.stock_address, sinfo.stock_code, sinfo.stock_name, IFNULL(goods.goods_num, 0) goods_num FROM tab_purchase_info pinfo LEFT JOIN tab_user_info uinfo ON pinfo.user_code = uinfo.user_code LEFT JOIN tab_customer_supplier csinfo ON pinfo.cust_code = csinfo.cust_code AND (csinfo.cust_type = 2 OR csinfo.cust_type = 3) LEFT JOIN tab_purchase_goods_detail goods ON pinfo.purchase_code = goods.purchase_code LEFT JOIN tab_stock_info sinfo ON sinfo.stock_code = goods.stock_code WHERE 1=1
AND pinfo.purchase_date >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
AND pinfo.purchase_date <= DATE_FORMAT(#{endDate},'%Y-%m-%d 23:59:59')
AND csinfo.id >= #{fromSupplierId}
AND csinfo.id <= #{toSupplierId}
ORDER BY pinfo.purchase_date DESC;
