mapper.select:
SELECT dict.dict_code, dict.dict_name, #{startDate} AS startDate, #{endDate} AS endDate, #{businessType} AS businessType FROM tab_dictionary_info dict
  WHERE dict.data_type='inventory_type'

        AND dict.dict_code = #{stockCode}

SELECT user_code, salesman_code, real_name, #{startDate} AS startDate, #{endDate} AS endDate, #{businessType} AS businessType FROM tab_user_info WHERE 1=1
AND user_code IN
#{item}

SELECT si.create_time, cs.cust_code, cs.cust_name, IFNULL((SELECT SUM(collected_amount) FROM tab_sale_receipts_details A WHERE si.sale_code = A.sale_code), 0) AS collected_amount_total, si.sale_code FROM tab_sale_info si LEFT JOIN tab_customer_supplier cs ON cs.cust_code = si.cust_code WHERE 1 = 1
AND si.user_code = #{user_code}
AND si.create_time BETWEEN #{startDate} AND #{endDate} AND si.sale_ticket_type = #{businessType}

SELECT cs.cust_code, cs.cust_name, #{startDate} AS startDate, #{endDate} AS endDate, #{businessType} AS businessType FROM tab_customer_supplier cs WHERE 1 = 1 AND cust_type IN (#{custType}, 3)
AND cs.id >= #{fromCustId}
AND cs.id <= #{toCustId}

SELECT si.create_time,
si.sale_code
FROM tab_sale_info si
WHERE 1 = 1

    AND si.cust_code = #{cust_code}

AND si.create_time BETWEEN #{startDate} AND #{endDate}
AND si.sale_ticket_type = #{businessType}

SELECT ui.user_code AS userCode, SUM(si.receivable_accoun) AS sumSaleVolume, /*销售额, 总金额-优惠金额*/ SUM(sg.goods_num) AS sumGoodsNum, /*数量*/ SUM(sg.goods_num * sg.unit_price) AS sumSaleCost, /*成本*/ SUM(si.receivable_accoun - sg.goods_num * sg.unit_price) AS sumGrossMargin, /*毛利*/ CONVERT(SUM(si.receivable_accoun - sg.goods_num * sg.unit_price) / SUM(si.receivable_accoun),DECIMAL(10, 4)) * 100 AS grossRate, SUM(sg1.goods_num) AS sumCost, /*额外支出费用*/ SUM(si.receivable_accoun) - SUM(sg1.goods_num) AS pureProfit, ui.commission_rate AS commissionRate, /*佣金率*/ IFNULL(ui.commission_rate * SUM(si.receivable_accoun), 0) / 100 AS commission, /*佣金*/ ui.real_name AS realName FROM tab_sale_info si LEFT JOIN tab_user_info ui ON ui.user_code = si.user_code inner JOIN tab_sale_goods_detail sg ON si.sale_code = sg.sale_code inner JOIN tab_sale_goods_detail sg1 ON si.sale_code = sg1.sale_code AND sg1.stock_code REGEXP '^cb' LEFT JOIN tab_sale_receipts_details sr ON si.sale_code = sr.sale_code WHERE 1 =1
AND si.user_code IN
#{item}
AND si.create_time BETWEEN #{startDate} AND #{endDate}
AND si.payment_status = 'cs03'
AND si.sale_ticket_type = #{businessType} GROUP BY si.user_code
