mapper.select:
SELECT sinfo.id, sgd.stock_code, sgd.stock_name, dinfo.dict_name, CASE WHEN sgd.real_sell_price > 0 THEN sgd.real_sell_price ELSE sgd.unit_price END unitPrice, snp.standard_buy_price, uinfo.real_name, sgd.goods_num, snp.stock_address, sinfo.sale_code, DATE_FORMAT(sinfo.sale_date, '%Y-%m-%d') sale_date,IFNULL(sinfo.discount_amount,0) discount_amount, cs.cust_code, cs.cust_name FROM tab_sale_info sinfo LEFT JOIN tab_sale_goods_detail sgd ON sinfo.sale_code = sgd.sale_code AND sgd.stock_code NOT REGEXP '^cb' LEFT JOIN tab_stock_info stinfo ON stinfo.stock_code = sgd.stock_code LEFT JOIN tab_user_info uinfo ON sinfo.user_code = uinfo.user_code LEFT JOIN tab_customer_supplier cs ON sinfo.cust_code = cs.cust_code AND cs.cust_type IN (1, 3) INNER JOIN tab_dictionary_info dinfo ON dinfo.dict_code = stinfo.stock_class AND dinfo.data_type = 'inventory_type' INNER JOIN tab_stock_num_price snp ON sgd.stock_code = snp.stock_code WHERE 1 =1
AND sinfo.sale_date >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
AND sinfo.sale_date <= DATE_FORMAT(#{endDate},'%Y-%m-%d 23:59:59')
AND stinfo.stock_class = #{stockClass}
AND stinfo.stock_code >= #{fromId}
AND stinfo.stock_code <= #{toId}
/*group by stinfo.id*/

SELECT sinfo.id, sinfo.sale_code, DATE_FORMAT(sinfo.sale_date, '%Y-%m-%d') sale_date, cs.cust_code, cs.cust_name, IFNULL(sinfo.discount_amount,0) discount_amount, IFNULL(sinfo.discount_amount,0) discount_amount, sgd.goods_num, CASE WHEN sgd.real_sell_price > 0 THEN sgd.real_sell_price ELSE sgd.unit_price END unitPrice, snp.standard_buy_price, sgd.stock_code, sgd.stock_name FROM tab_sale_info sinfo LEFT JOIN tab_customer_supplier cs ON sinfo.cust_code = cs.cust_code LEFT JOIN tab_sale_goods_detail sgd ON sinfo.sale_code = sgd.sale_code INNER JOIN tab_stock_num_price snp ON sgd.stock_code = snp.stock_code WHERE 1 = 1
AND sinfo.sale_date >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
AND sinfo.sale_date <= DATE_FORMAT(#{endDate},'%Y-%m-%d 23:59:59')
AND cs.id >= #{fromCustId}
AND cs.id <= #{toCustId}
AND cs.cust_code IS NOT NULL

SELECT sinfo.sale_code, DATE_FORMAT(sinfo.sale_date, '%Y-%m-%d') sale_date, sinfo.salesman_code, sinfo.exchange_rate, IFNULL(sinfo.discount_amount,0) discount_amount, uinfo.real_name, cs.cust_code, cs.cust_name, IFNULL(sgd.goods_num, 0) AS goods_num, CASE WHEN sgd.real_sell_price > 0 THEN sgd.real_sell_price ELSE sgd.unit_price END unitPrice, sgd.stock_address, sgd.stock_code,
sgd.stock_name FROM tab_sale_info sinfo LEFT JOIN tab_sale_goods_detail sgd ON sinfo.sale_code = sgd.sale_code LEFT JOIN tab_customer_supplier cs ON sinfo.cust_code = cs.cust_code AND (cs.cust_type = 1 OR cs.cust_type = 3) LEFT JOIN tab_user_info uinfo ON sinfo.user_code = uinfo.user_code WHERE 1 = 1
AND sinfo.sale_date >= DATE_FORMAT(#{startDate},'%Y-%m-%d %H:%i:%s')
AND sinfo.sale_date <= DATE_FORMAT(#{endDate},'%Y-%m-%d 23:59:59')
AND cs.id >= #{fromCustId}
AND cs.id <= #{toCustId}
AND sinfo.payment_status IN
#{item}
ORDER BY sinfo.sale_date

SELECT cs.cust_grade, cs.cust_code AS custCode, cs.cust_name AS custName, uinfo.real_name AS realName, cs.totalVolume AS saleVolume, cs.buyCount AS buyCount FROM tab_customer_supplier cs /* LEFT JOIN tab_sale_info sinfo ON cs.cust_code = sinfo.cust_code*/ LEFT JOIN tab_user_info uinfo ON cs.user_code = uinfo.user_code WHERE cs.cust_type IN (1, 3) AND (cs.totalVolume >= 12000 OR cs.buyCount >= 5) AND cs.cust_code NOT REGEXP '^CB'
AND cs.user_code=#{userCode}
AND cs.cust_code=#{custCode}
AND cs.cust_name LIKE CONCAT('%', #{custName}, '%')
AND cs.grade_code IN
#{item}
GROUP BY cs.cust_code ORDER BY cs.totalVolume DESC,cs.buyCount DESC

SELECT u.real_name,s.salesman_code, IFNULL(COUNT(s.salesman_code), 0) AS countTicket FROM tab_sale_info s LEFT JOIN tab_user_info u ON s.user_code = u.user_code WHERE 1= 1
AND s.sale_date >= DATE_FORMAT(#{startTime},'%Y-%m-%d %H:%i:%s')
AND s.sale_date <= DATE_FORMAT(#{endTime}, '%Y-%m-%d 23:59:59')
AND s.user_code=#{userCode}
AND s.subordinate_company_code=#{subordinateCompanyCode}
AND s.salesman_code IS NOT NULL GROUP BY s.user_code ORDER BY countTicket DESC
